ruleSearch.bookList:
class.list-group-item!0:-1<js>
result
  .toArray()
  .slice(0, 5)
  .map(function (el) {
    var url = String(el.select('a').eq(0).attr('href')).replace(
      /^\/([^\/])/,
      baseUrl.match(/(.+\/\/.+?\/)/)[1] + '$1'
    );
    var J = org.jsoup.Jsoup.parse(String(java.ajax(url)));
    var og = (selector) =>
      J.select('[property="og:' + selector + '"]').attr('content');

    return {
      name: og('novel:book_name'),
      author: og('novel:author'),
      kind: og('novel:category') + ',' + og('novel:status'),
      latest: og('novel:latest_chapter_name') + ' • ' +og('novel:update_time'),
      cover: og('image'),
      intro: og('description'),
      url: og('novel:read_url'),
    };
  });
</js>

