loginInfo = source.getLoginInfoMap();
loginInfo == null
  ? v = ""
  : v =
      loginInfo["Host"] == undefined || loginInfo["Host"] == ""
        ? ""
        : loginInfo["Host"];
loginInfo == null
  ? Pages = 300
  : Pages =
      loginInfo["Pages"] == undefined || loginInfo["Pages"] == ""
        ? 300
        : loginInfo["Pages"];
loginInfo == null
  ? Content_Check = "false"
  : Content_Check =
      loginInfo["Check"] == undefined || loginInfo["Check"] == ""
        ? "false"
        : loginInfo["Check"];
loginInfo == null
  ? Error = 10
  : Error =
      loginInfo["Error"] == undefined || loginInfo["Error"] == ""
        ? 10
        : loginInfo["Error"];
host =
  v == 1
    ? "https://www.wshm.uk"
    : v == 2
    ? "https://www.wshm22.com"
    : v == 3
    ? "https://www.wshm.net"
    : v == 4
    ? "https://www.wshm27.com"
    : "https://www.wshm.uk";
headers = `{
  "v": ${v};
  "Host": ${host};
  "Pages": ${Pages};
  "Check": ${Content_Check };
  "Error": ${Error}
}`
source.putLoginHeader(headers);

